def removeExtras(arr):
	for x in range(len(arr)):
		for char in arr[x]:
			if char in ".'":
				arr[x] = arr[x].replace(char,'')
	for x in range(len(arr)):
		if 'jr' in arr[x].lower():
			arr[x] = arr[x].replace(' Jr','')
		if 'II' in arr[x]:
			arr[x] = arr[x].replace(' II','')
		if 'IV' in arr[x]:
			arr[x] = arr[x].replace(' IV','')
		if 'Fuller V' in arr[x]:
			arr[x] = arr[x].replace(' V','')
		if 'III' in arr[x]:
			arr[x] = arr[x].replace(' III','')
		if 'Sr' in arr[x]:
			arr[x] = arr[x].replace(' Sr','')	
		if 'Mitchell' in arr[x]:
			arr[x] = arr[x].replace('Mitchell','Mitch')
	return arr


def indexItem(start, source, x):
	try:
		return source.index(start[x]) + 1
	except:
		return '-'

def ranks(start, src2, src3, src4, src5):
	for x in range(len(start)):
		total = indexItem(start, start, x)
		count = 1
		std = 0
		stdArr = []
		stdArr.append(total)
		for y in (src2, src3, src4, src5):
			try:
				total += indexItem(start, y, x)
				count += 1
				stdArr.append(indexItem(start, y, x))
			except:
				pass

		avg = round(total/count,2)
		if len(stdArr) > 1:
			for z in range(len(stdArr)):
				std += (stdArr[z] - avg)**2
			std = (std/(len(stdArr) - 1))**.5

		holdArray.append({'name' : start[x], 'key' : x, 'YAH' : indexItem(start, start, x), 'CBS' : indexItem(start, src2, x), 'ESP' : indexItem(start, src3, x), 'FOX' : indexItem(start, src4, x), 'NFL' : indexItem(start, src5, x), 'AVG' :avg, 'STD' :round(std,2)})


